import {
  BrowserWindow,
  app,
  ipcMain,
  desktopCapturer,
  session,
  globalShortcut,
  dialog
} from "electron";
import screenshot from 'screenshot-desktop';
// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;
import Prism from "prismjs";
import OpenAI from "openai";
import axios from "axios";
import FormData from "form-data";
import fs from "fs";
import path from "path";
import { Buffer } from "buffer";
import { createClient, LiveTranscriptionEvents } from "@deepgram/sdk";

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
import electronSquirrelStartup from "electron-squirrel-startup";

if (electronSquirrelStartup) {
  app.quit();
}

let mainWindowInstance: BrowserWindow | null = null;

const createWindow = (): void => {
  mainWindowInstance = new BrowserWindow({

    height: 1000,
    width: 1300,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
      contextIsolation: true,
      nodeIntegration: false,
      webSecurity: false,
    },
  });

  mainWindowInstance.webContents.session.webRequest.onHeadersReceived(
    (details: Electron.OnHeadersReceivedListenerDetails, callback: (response: any) => void) => {
      const currentHeaders = details.responseHeaders || {};
      callback({
        cancel: false,
        responseHeaders: {
          ...currentHeaders,
          "Content-Security-Policy": [
            "default-src 'self' 'unsafe-inline' 'unsafe-eval' data: blob:; script-src 'self' 'unsafe-inline' 'unsafe-eval' blob:; connect-src 'self' https://api.openai.com;",
          ],
        }
      });
    }
  );

  mainWindowInstance.webContents.session.setPermissionRequestHandler(
    (webContents: Electron.WebContents, permission: string, callback: (permissionGranted: boolean) => void) => {
      if (permission === "media") {
        callback(true);
      } else {
        callback(false);
      }
    }
  );

  mainWindowInstance.loadURL(MAIN_WINDOW_WEBPACK_ENTRY + "#/main_window");

  // Set content protection
  if (process.platform === 'darwin' || process.platform === 'win32') {
    mainWindowInstance.setContentProtection(true);
    console.log('Content protection enabled.');
  } else {
    console.log('Content protection is not supported on this platform.');
  }
//  mainWindow.webContents.openDevTools();

  mainWindowInstance.webContents.on("did-finish-load", () => {
    mainWindowInstance.webContents.executeJavaScript(`
      console.log('Applied CSP:', document.querySelector('meta[http-equiv="Content-Security-Policy"]')?.getAttribute('content'));
    `);
  });
};

ipcMain.handle(
  "save-temp-audio-file",
  async (event, audioBuffer: ArrayBuffer) => {
    try {
      const tempFilePath = path.join(
        app.getPath("temp"),
        `temp_audio_${Date.now()}.wav`
      );
      fs.writeFileSync(tempFilePath, Buffer.from(audioBuffer));
      return tempFilePath;
    } catch (error) {
      throw error;
    }
  }
);

ipcMain.handle(
  "transcribe-audio-file",
  async (event, filePath: string, config) => {
    try {
      const formData = new FormData();
      formData.append("file", fs.createReadStream(filePath), "audio.wav");
      formData.append("model", "whisper-1");

      if (config.primaryLanguage && config.primaryLanguage !== "auto") {
        formData.append("language", config.primaryLanguage);
      }
      if (config.secondaryLanguage) {
        formData.append(
          "prompt",
          `This audio may contain ${config.primaryLanguage} and ${config.secondaryLanguage}.`
        );
      }

      const baseUrl = normalizeApiBaseUrl(config.api_base);
      const apiUrl = `${baseUrl}/audio/transcriptions`;
      const response = await axios.post(apiUrl, formData, {
        headers: {
          ...formData.getHeaders(),
          Authorization: `Bearer ${config.openai_key}`,
        },
        maxContentLength: Infinity,
        maxBodyLength: Infinity,
      });

      return response.data;
    } catch (error) {
      throw error;
    } finally {
      fs.unlinkSync(filePath);
    }
  }
);

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on("ready", () => {
  createWindow();
  globalShortcut.register('CommandOrControl+Shift+S', handleScreenshotShortcut);
});

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the
// dock icon is clicked and there are no other windows open.
app.on("window-all-closed", () => {
  if (process.platform !== "darwin") {
    app.quit();
  }
});

app.on("activate", () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

app.on('will-quit', () => {
  // Unregister all shortcuts.
  globalShortcut.unregisterAll();
});

async function handleScreenshotShortcut() {
  if (!mainWindowInstance) return;

  try {
    mainWindowInstance.hide();
    // Add a small delay to ensure the window is hidden
    await new Promise(resolve => setTimeout(resolve, 250));

    const screenshotResult = await screenshot({ format: 'png' });

    if (!Buffer.isBuffer(screenshotResult)) {
      console.error('Screenshot failed: did not return a Buffer.');
      if (mainWindowInstance && !mainWindowInstance.isDestroyed()) {
        dialog.showErrorBox('Screenshot Error', 'Failed to get image data for screenshot.');
      }
      // Ensure window is shown even if screenshot fails before save dialog
      if (mainWindowInstance && !mainWindowInstance.isDestroyed() && !mainWindowInstance.isVisible()) {
        mainWindowInstance.show();
      }
      return;
    }
    const imgBuffer: Buffer = screenshotResult;

    if (!mainWindowInstance || mainWindowInstance.isDestroyed()) {
        console.error('Main window not available for save dialog.');
        const errorResult = await dialog.showSaveDialog({
            title: 'Save Screenshot',
            defaultPath: path.join(app.getPath('pictures'), `screenshot-${Date.now()}.png`),
            filters: [{ name: 'PNG Images', extensions: ['png'] }],
        });
        // This block is tricky because imgBuffer might not be defined if the above check failed.
        // However, the check for !mainWindowInstance implies a different flow. Assuming imgBuffer is valid here if we reached this point.
        // For safety, let's re-check, though ideally the control flow should prevent this.
        if (errorResult.filePath && Buffer.isBuffer(screenshotResult)) { // screenshotResult is in wider scope
            await fs.promises.writeFile(errorResult.filePath, screenshotResult as Uint8Array);
            console.log(`Screenshot saved to ${errorResult.filePath} (no parent window)`);
        } else if (errorResult.filePath) {
            console.error('Attempted to save screenshot without valid buffer data (no parent window).');
        }
        return; 
    }

    const { filePath, canceled } = await dialog.showSaveDialog(mainWindowInstance, {
      title: 'Save Screenshot',
      defaultPath: path.join(app.getPath('pictures'), `screenshot-${Date.now()}.png`),
      filters: [{ name: 'PNG Images', extensions: ['png'] }],
    });

    if (!canceled && filePath) {
      await fs.promises.writeFile(filePath, imgBuffer as Uint8Array);
      console.log(`Screenshot saved to ${filePath}`);
    }
  } catch (error) {
    console.error('Failed to capture or save screenshot:', error);
    if (mainWindowInstance && !mainWindowInstance.isDestroyed()) {
        dialog.showErrorBox('Screenshot Error', `Failed to capture or save screenshot: ${error.message}`);
    } else {
        dialog.showErrorBox('Screenshot Error (No Window)', `Failed to capture or save screenshot: ${error.message}`);
    }
  } finally {
    if (mainWindowInstance && !mainWindowInstance.isDestroyed() && !mainWindowInstance.isVisible()) {
        mainWindowInstance.show();
    }
  }
}

